const CDN = "https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/items";

export function getItemDetails(itemId: number): ItemData | null {
  return ITEM_DETAILS[itemId] ?? null;
}

export function getItemName(itemId: number): string {
  return ITEM_DETAILS[itemId]?.name ?? `Item ${itemId}`;
}

export function getItemIcon(itemId: number): string {
  const img = ITEM_DETAILS[itemId]?.img;
  if (!img) return "";
  return `${CDN}/${img}.png`;
}
